INSERT INTO items (code, item_desc, recommendations, is_mandatory, "section_id") VALUES
('T-01', 'As finalidades de tratamento de dados foram definidas na organização?', 'Crie um documento (Política de Privacidade) descrevendo os objetivos e a forma de utilização dos dados.', true, 1),
('T-02', 'O tratamento de dados pessoais é realizado de acordo com uma base legal?', 'Na Política de Privacidade, explique a base legal do tratamento (ex: consentimento, obrigação legal). Consulte o Art. 7 da LGPD para a lista completa.', true, 1),
('T-03', 'O sistema informa ao titular sobre as finalidades de tratamento de dados pessoais?', 'O sistema deve disponibilizar a Política de Privacidade ou Termos de Consentimento para o titular.', true, 1),
('T-04', 'O sistema realiza o tratamento de dados em conformidade com a finalidade apresentada ao titular?', 'Revise o consentimento e implemente as alterações necessárias para garantir a conformidade.', true, 1),
('T-05', 'O sistema informa ao titular a forma e duração do tratamento dos seus dados de maneira gratuita e acessível?', 'Descreva na Política de Privacidade a forma e a duração do tratamento dos dados, mantendo a informação sempre acessível.', true, 1),
('T-06', 'O sistema permite o titular consultar sobre a integralidade dos seus dados pessoais de maneira gratuita e acessível?', 'Crie um canal de comunicação (e-mail, página de contato) para que o titular possa requisitar seus dados de forma gratuita.', true, 1),
('T-07', 'O sistema armazena com exatidão e clareza os dados pessoais coletados dos titulares?', 'Durante a coleta, garanta a correção dos dados. Use validação de campos nos formulários (CPF, CEP, etc).', true, 1),
('T-08', 'O sistema mantém atualizados os dados pessoais, conforme necessário para a finalidade de seu tratamento?', 'Crie um processo para mapear e revisar os dados. Se descobrir dados incorretos, corrija ou apague-os rapidamente.', true, 1),
('T-09', 'O sistema disponibiliza ao titular as informações sobre o tratamento e a identidade do controlador?', 'Disponibilize na Política de Privacidade informações claras sobre: finalidades, identidade do controlador, dados envolvidos, base legal, etc.', true, 1),
('T-10', 'A organização realiza o tratamento baseado no legítimo interesse, de forma adequada com a lei?', 'A empresa deve realizar uma análise (LIA) para verificar se a hipótese se enquadra na base legal do legítimo interesse.', true, 1),
('T-11', 'O sistema mantém registros das operações de tratamento de dados, especialmente quando baseado em legítimo interesse?', 'Armazene no banco de dados os logs das operações de tratamento de dados para possíveis comprovações legais.', true, 1),
('T-12', 'O sistema informa ao titular e autoridades sobre o tratamento, quando baseado em legítimo interesse?', 'Elabore relatórios das operações de tratamento de dados e disponibilize-os para o titular e autoridades (ANPD) quando requisitado.', true, 1),
('T-13', 'O sistema realiza o tratamento de forma que os dados não possam ser conectados a um titular, quando requisitado?', 'Analise quais dados devem ser anonimizados ou pseudonimizados. Ex: uma versão dos dados sem revelar a identidade real do titular.', true, 1),
('T-14', 'O sistema informa ao titular sobre a existência de tratamento dos seus dados, antes da coleta?', 'Disponibilize ao titular, com destaque, avisos sobre os dados que serão coletados e solicite seu consentimento.', true, 1),
('C-01', 'O sistema permite que o titular forneça seu consentimento de forma autônoma e clara?', 'Use linguagem clara, evite caixas pré-marcadas e ofereça uma opção para negar ou retirar o consentimento sem prejuízos.', true, 2),
('C-02', 'O sistema solicita consentimento específico para compartilhar dados com outros controladores?', 'Seja transparente sobre o compartilhamento de dados com terceiros, informando o titular de forma clara no consentimento.', true, 2),
('C-03', 'O sistema armazena o consentimento dado pelo titular para comprovações legais?', 'Armazene no banco de dados os registros (logs) de consentimento do titular para comprovação.', true, 2),
('C-04', 'O sistema permite ao titular meios para recusar ou retirar o consentimento sem prejuízo?', 'O titular deve ter acesso ao seu termo de consentimento ou contrato com a opção de negar ou revogar o tratamento de dados.', true, 2),
('C-05', 'O tratamento de dados de crianças e adolescentes tem consentimento específico dos pais ou responsável legal?', 'Certifique que apenas o responsável legítimo pode dar o consentimento e atualizá-lo. Considere usar autenticação do titular.', true, 2),
('C-06', 'O sistema informa o titular sobre mudanças de finalidade, permitindo a revogação do consentimento?', 'Sempre que alterar a finalidade, informe o titular e solicite novo consentimento, com a opção de negar a alteração.', true, 2),
('C-07', 'A declaração de consentimento é inteligível, acessível e não contém termos abusivos?', 'O Termo de Consentimento deve ser bem escrito, com linguagem de fácil compreensão e respeitando os direitos do titular.', true, 2),
('C-08', 'O sistema informa o titular de dados sobre todos os seus direitos no consentimento?', 'Informe no Termo de Consentimento e na Política de Privacidade a lista de direitos que os titulares de dados possuem.', true, 2),
('D-01', 'O sistema armazena os dados pessoais em formato que facilite o acesso do titular?', 'Armazene os dados em formatos estruturados e comuns (CSV, JSON, XML) para que possam ser facilmente extraídos.', true, 3),
('D-02', 'O sistema fornece ao titular meios para registrar reclamações sobre o tratamento de seus dados?', 'Disponibilize o contato do DPO ou um canal exclusivo para reclamações na política de privacidade.', true, 3),
('D-03', 'O sistema fornece a cópia eletrônica integral dos dados pessoais para o titular?', 'Transmitir diretamente os dados solicitados ao titular ou fornecer acesso a uma ferramenta que permita a extração pelo próprio usuário.', true, 3),
('D-04', 'O sistema permite que o titular atualize seus dados pessoais?', 'Crie um mecanismo para que o próprio titular possa atualizar seus dados pessoais, como uma página de perfil.', true, 3),
('D-05', 'O sistema utiliza apenas dados relevantes e adequados à finalidade (Minimização)?', 'Realize a minimização dos dados, utilizando apenas o que for relevante. Considere anonimizar ou eliminar dados desnecessários.', true, 3),
('D-06', 'O sistema permite a portabilidade de dados a outro controlador mediante requisição do titular?', 'Os dados devem ser armazenados em formato legível por máquina para permitir a exportação quando o titular requisitar.', true, 3),
('D-07', 'O sistema permite excluir os dados pessoais do titular, mediante a sua requisição?', 'Crie um mecanismo para que o titular possa solicitar a remoção permanente de seus dados pessoais.', true, 3),
('D-08', 'O sistema remove os dados pessoais do titular após o término de seu tratamento?', 'É necessário excluir os dados pessoais do titular após o cumprimento da finalidade, no prazo estabelecido.', true, 3),
('D-09', 'O sistema fornece informações sobre entidades públicas e privadas, caso tenha realizado compartilhamento?', 'Armazene as informações das entidades e das operações de tratamento para comprovações legais e disponibilize quando requisitado.', true, 3),
('D-10', 'O sistema informa o titular sobre a possibilidade de não fornecer consentimento e as consequências?', 'Deve fornecer no Termo de Consentimento a possibilidade de recusar o tratamento, e as consequências da negativa.', true, 3),
('D-11', 'O sistema informa quando o tratamento de dados é condição para fornecimento de produto ou serviço?', 'Deve ficar claro no momento do consentimento do titular os objetivos e pré-requisitos do tratamento de dados.', true, 3),
('D-12', 'O sistema permite aos titulares, o direito de se opor a um tratamento de dados de forma fácil?', 'É necessário fornecer um mecanismo para que o titular consiga retirar seu consentimento. Caso retirado, os dados não poderão ser mais processados.', true, 3),
('D-13', 'O sistema informa ao titular, as razões do não exercício imediato de seus direitos?', 'Quando não for possível atender imediatamente, deve-se comunicar o motivo (de fato ou de direito) ou indicar o agente de tratamento responsável.', true, 3),
('D-14', 'O sistema informa o titular quando uma decisão é tomada com base no tratamento automatizado?', 'Disponibilize para o titular as informações das decisões automatizadas que serão realizadas. Forneça o registro se requisitado.', true, 3),
('D-15', 'O sistema fornece a opção do titular contestar ou solicitar revisão da decisão automatizada?', 'Caso seja requisitado, deverá revisar a decisão automatizada contestada, podendo interromper o tratamento.', true, 3),
('S-01', 'O sistema realiza o tratamento de dados de forma segura, incluindo proteção contra acesso não autorizado?', 'Garanta a segurança no tratamento, possibilitando apenas indivíduos autorizados a ler, modificar ou excluir dados (Controle de Acesso).', true, 4),
('S-02', 'O sistema respeita as normas de transferência internacional de dados para países com proteção adequada?', 'Verifique se o país de destino possui nível de proteção de dados adequado à LGPD e se a ANPD autoriza tais operações.', true, 4),
('S-03', 'O sistema informa o titular sobre as transferências internacionais de seus dados pessoais?', 'Informar no documento da Política de Privacidade e no Termo de Consentimento, sobre a realização de transferências de dados.', true, 4),
('S-04', 'O sistema utiliza mecanismos para prevenir a ocorrência de danos, destruição ou perda de dados?', 'Implemente boas práticas como backup de dados, armazenamento em nuvem e outras ações para proteger as informações.', true, 4),
('S-05', 'O sistema utiliza medidas de proteção adequadas para dados pessoais sensíveis?', 'Implemente uma camada adicional de segurança, como criptografia e controle de acesso restrito, para dados sensíveis.', true, 4),
('R-01', 'A organização indica um oficial de proteção de dados (DPO) encarregado pelo tratamento de dados?', 'A organização deve indicar um DPO para atuar como canal de comunicação com os titulares e a ANPD.', true, 5),
('R-02', 'A organização divulga publicamente a identidade e as informações de contato do DPO?', 'A informação deve estar de forma clara na Política de Privacidade para que os titulares e a autoridade nacional tenham acesso facilitado.', true, 5),
('R-03', 'A organização comunica à ANPD e ao titular a ocorrência de incidente de segurança relevante?', 'Comunique incidentes à ANPD em até 2 dias úteis da ciência. O conteúdo deve seguir o Art. 48 da LGPD.', true, 5),
('R-04', 'A organização realiza avaliação de impacto à proteção de dados (AIPD) para tratamentos de alto risco?', 'A organização deve realizar uma AIPD quando existir elevado risco no tratamento. A avaliação deve ser sempre revisada.', true, 5),
('R-05', 'A organização realiza e disponibiliza o relatório de impacto à proteção de dados (RIPD) à ANPD?', 'O relatório deve conter a descrição dos dados, metodologia, garantias de segurança e análise de mitigação de riscos.', true, 5),
('S-06', 'O sistema utiliza boas práticas como privacy by design?', 'Como boa prática para o desenvolvimento e adequação do sistema, implementar a metodologia privacy by design.', false, 4),
('S-07', 'O sistema realiza o mapeamento dos dados pessoais e mantém em segurança?', 'Organizar os locais onde os dados são coletados e armazenados.', false, 4),
('S-08', 'O sistema fornece a confidencialidade, usando medidas técnicas apropriadas?', 'Realizar a proteção contra acesso não autorizado no uso de dados, como autenticação e controle de acesso.', false, 4),
('S-09', 'O sistema fornece a integridade dos dados pessoais, impedindo modificações não autorizadas?', 'Verificar a integridade dos dados (ex: checksums) durante a coleta e no tratamento dos dados pessoais.', false, 4),
('S-10', 'O sistema mantém registros de auditorias de conformidades e disponibiliza ao titular, se requisitado?', 'Uma solução seria documentar os dados das auditorias realizadas para fornecer ao titular.', false, 4),
('S-11', 'A organização possui um plano de correção a incidentes de privacidade e segurança?', 'A organização deve documentar um plano para correção de falhas ou incidentes de segurança.', false, 4),
('S-12', 'O sistema possui certificação ou selos para comprovar a conformidade com as normas de proteção de dados?', 'É recomendável que o sistema tenha uma certificação (ex: ISO 27001, ISO 27701) que comprove a aderência à lei.', false, 4),
('S-13', 'O dispositivo possui certificação para comprovar os padrões de qualidade (ex: Anatel)?', 'Certificações comprovam que o dispositivo possui níveis de qualidade e segurança determinados pela regulamentação.', false, 4),
('S-14', 'O dispositivo é seguro contra ataques de força bruta ou outras tentativas abusivas de login?', 'Uma solução é criar mecanismo para bloquear os utilizadores após várias tentativas inválidas de acesso.', false, 4),
('S-15', 'O dispositivo possui mecanismos contra ataques conhecidos (ex: buffer overflow, DDoS)?', 'Realizar uma bateria de testes de ataques mais conhecidos atualmente como forma de prevenção.', false, 4),
('S-16', 'Os dispositivos utilizam técnicas de proteção para realizar uma comunicação segura entre si?', 'Ao compartilhar os dados entre os dispositivos, é necessário que técnicas de proteção sejam implementadas.', false, 4),
('S-17', 'As senhas ficam encriptadas na base de dados do dispositivo?', 'Se as senhas salvas estiverem criptografadas, se tornam mais seguras contra acesso indevido ao dispositivo.', false, 4),
('S-18', 'O dispositivo encaminha os dados devidamente para o seu destino correto?', 'Realizar verificações para identificar se os dados estão sendo encaminhados apenas para seu devido destino.', false, 4),
('S-19', 'O dispositivo utiliza protocolos como TLS para criptografar as comunicações?', 'Os protocolos TLS servem para criptografar as comunicações, proporcionando segurança na troca de dados.', false, 4),
('S-20', 'A organização cria mecanismos de segurança para os dispositivos com pouco processamento?', 'Uma alternativa é a utilização de gateways, para realizar o tratamento que o dispositivo restrito não consegue.', false, 4),
('S-21', 'O dispositivo recebe atualizações de segurança?', 'As atualizações de segurança corrigem falhas e diminuem os riscos contra invasões.', false, 4),
('S-22', 'O dispositivo encripta os dados no armazenamento para evitar a identificação dos dados pessoais?', 'Ao armazenar os dados com criptografia, eles ficarão mais protegidos em caso de violação.', false, 4),
('R-06', 'Existe um profissional de segurança fiscalizando atividades suspeitas ou incomuns?', 'Ter um profissional fiscalizando todo o processo diminuirá as chances de um incidente de dados.', false, 5),
('S-23', 'Os dispositivos são alocados em uma VLAN específica, separando-se de outras redes?', 'Realizar o gerenciamento da rede, separando o acesso de usuários/visitantes da rede dos dispositivos.', false, 4),
('S-24', 'A organização recebe notificação quando a senha do dispositivo é trocada?', 'A notificação permite que a organização tome medidas protetivas imediatamente em caso de acesso não autorizado.', false, 4),
('S-25', 'A organização recebe notificações em caso de acessos não autorizados?', 'Criar mecanismos para notificações se ocorrer acessos indevidos, para agir imediatamente em caso de invasões.', false, 4),
('R-07', 'A organização registra as atividades realizadas pelo DPO para comprovações legais?', 'Armazene e documente o histórico de atividades do encarregado DPO.', false, 5),
('R-08', 'A organização realiza capacitação para seus funcionários sobre a aderência à LGPD?', 'Capacite os funcionários sobre as informações que a LGPD determina através de treinamentos, cursos e palestras.', false, 5),
('R-09', 'São realizadas capacitações para os funcionários que utilizam os dispositivos?', 'Realizar capacitações com os funcionários para garantir a qualidade e eficiência de todo o processo.', false, 5),
('A-01', 'A organização fornece credenciais diferentes para cada utilizador?', 'Deve ser realizado o controle de acesso para cada utilizador, criando uma lista de pessoas autorizadas.', false, 6),
('A-02', 'São atribuídas senhas diferentes a cada dispositivo para fortalecer a segurança?', 'Utilizar a mesma senha enfraquece a segurança. O ideal é ter senhas diferentes, talvez geradas por um algoritmo.', false, 6),
('A-03', 'Houve mudança da senha padrão do dispositivo para evitar acesso não autorizado?', 'É de suma importância a mudança imediata da senha padrão do dispositivo, que é facilmente encontrada no manual.', false, 6),
('A-04', 'O dispositivo garante que a senha seja forte e segura?', 'Para maior proteção, é fundamental senhas fortes (letras maiúsculas, minúsculas, números e símbolos).', false, 6),
('A-05', 'São registradas falhas ou possíveis suspeitas de vulnerabilidade?', 'Realizar registros de todas as falhas e suspeitas reais. Possuir esta documentação ajuda na organização e prevenção.', false, 6),
('SF-01', 'O dispositivo possui apenas portas em funcionamento, não permitindo portas desnecessárias?', 'Todas as portas que não estão em uso, deverão ser desativadas. Exemplos de portas: RJ-45 e USB.', false, 7),
('SF-02', 'O dispositivo não possibilita o acesso físico de pessoas não autorizadas?', 'Analisar o ambiente no qual o dispositivo se encontra, analisando possíveis riscos que o dispositivo poderá sofrer.', false, 7),
('SF-03', 'O dispositivo é protegido contra tentativas de ser resetado para configuração de fábrica?', 'Apenas pessoas autorizadas devem ter acesso físico ao botão de reset. Se não for restrito, uma solução é a blindagem do equipamento.', false, 7),
('SF-04', 'O dispositivo é resistente para o ambiente que está alocado?', 'Realizar uma análise das condições ambientais (temperatura, umidade), identificando se afetarão o dispositivo.', false, 7),
('SF-05', 'A manutenção do dispositivo é realizada conforme recomendação do fornecedor?', 'O ideal é que as manutenções sejam realizadas nos intervalos e de acordo com as recomendações do fornecedor.', false, 7),
('SF-06', 'A manutenção e consertos dos dispositivos são realizados por uma equipe autorizada?', 'Para a confiabilidade do procedimento, é de grande importância ser realizado por uma equipe autorizada.', false, 7),
('SF-07', 'São registradas todas as manutenções preventivas?', 'A manutenção preventiva é a garantia do bom funcionamento e o registro se torna a comprovação e organização do procedimento.', false, 7);
